import React from "react";
import Modal from "react-modal";
import EditTicket from "./addData/EditTicket";
import DeleteTicket from "./addData/DeleteTicket";

const customStyles = {
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(44, 47, 51, 0.75)",
  },
  content: {
    paddingLeft: "100px",
    paddingRight: "100px",
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    background: "var(--dark)",
  },
};

// Make sure to bind modal to your appElement (http://reactcommunity.org/react-modal/accessibility/)
Modal.setAppElement(document.getElementById("root"));

const ModalEdit = (props) => {
  var subtitle;
  const [modalIsOpen, setIsOpen] = React.useState(false);
  // console.log(props);
  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "#f00";
  }

  function closeModal() {
    setIsOpen(false);
  }

  return (
    <div>
      <button className="editButton" onClick={openModal}>
        edit
      </button>
      <Modal isOpen={modalIsOpen} onAfterOpen={afterOpenModal} onRequestClose={closeModal} style={customStyles} contentLabel="Example Modal">
        <h2 ref={(_subtitle) => (subtitle = _subtitle)}>edit/delete</h2>
        <EditTicket value={props} />
        <div className="">
          <DeleteTicket value={props} />
          <button className="closeModal" onClick={closeModal}>
            close
          </button>
        </div>
      </Modal>
    </div>
  );
};

export default ModalEdit;
