import React from "react";
import { useContext, useState, useEffect } from "react";
import { TicketContext } from "../contexts/TicketContext";
import uuid from "uuid/v1";
import axios from "axios";
//props takes in data from Link, includes data like location /create
const AddBacklog = (props) => {
  const [tickets, setTickets] = useContext(TicketContext);
  const [ticket, setTicket] = useState({
    ticket: {
      _id: "",
      ticket_name: "",
      ticket_status: "",
    },
  });
  useEffect(() => {
    setTicket({
      ...ticket,
      ticket: {
        _id: "id1",
        ticket_name: "name1",
        ticket_status: "status1",
      },
    });
  }, []);

  const [name, setName] = useState("");

  const handleChange = (e) => {
    setName(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    let newTicket = {
      ticket_name: name,
      ticket_status: "backlog",
    };

    let id1 = "";
    let name1 = "";
    let status1 = "";

    axios.post("http://localhost:4000/tickets/add", newTicket).then((res) => {
      id1 = res.data.ticket._id;
      name1 = res.data.ticket.ticket_name;
      status1 = res.data.ticket.ticket_status;

      setTicket({
        ...ticket,
        ticket: {
          _id: id1,
          ticket_name: name1,
          ticket_status: status1,
        },
      });

      console.log(id1, name1, status1);
      console.log(ticket);
    });

    // console.log("meee", data.ticket);
    // console.log("meee", data.ticket._id);
    console.log(ticket);
    setTickets((currentTickets) => [...currentTickets, ticket]);

    setName("");
    // props.history.push("/");
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="addTicket">
        <label>
          <p>Add Ticket:</p>
          <input type="text" name="name" value={name} onChange={handleChange} />
        </label>
        <input type="submit" name="Submit" className="submit"></input>
      </div>
    </form>
  );
};

export default AddBacklog;
