import React from "react";
import { useContext, useState } from "react";
import { TicketContext } from "../contexts/TicketContext";
import ChangeStatus from "../addData/ChangeStatus";
import Ticket from "../Ticket";
import { Link } from "react-router-dom";
import axios from "axios";

const TicketDisplay = () => {
  const [tickets, setTickets] = useContext(TicketContext);
  console.log(tickets);

  const componentDidMount = () => {
    axios
      .get("http://localhost:4000/tickets/")
      .then((response) => {
        this.setState({ tickets: response.data });
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  return (
    <div className="display">
      <div className="backlog">
        <h1>Backlog</h1>
        {tickets.map((ticket, index) => (
          <div key={ticket.id}>
            {ticket.status === "backlog" && (
              <div className="ticketBlock">
                <button
                  onClick={(e) => {
                    const newTickets = tickets.slice();
                    newTickets.splice(index, 1, {
                      ...ticket,
                      status: "sprint",
                    });
                    setTickets(newTickets);
                  }}
                >
                  push
                </button>
                &nbsp;
                <Ticket name={ticket.name} status={ticket.status} key={ticket.id} />
              </div>
            )}
          </div>
        ))}
      </div>

      <div className="sprint">
        <h1>Sprint</h1>
        <div>
          {tickets.map((ticket, index) => (
            <div key={ticket.id}>
              {ticket.status === "sprint" && (
                <div className="ticketBlock">
                  <button
                    onClick={(e) => {
                      const newTickets = tickets.slice();
                      newTickets.splice(index, 1, {
                        ...ticket,
                        status: "progress",
                      });
                      setTickets(newTickets);
                    }}
                  >
                    push
                  </button>
                  &nbsp;
                  <Ticket name={ticket.name} status={ticket.status} key={ticket.id} />
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
      <div className="progress">
        <h1>Progress</h1>
        <div>
          {tickets.map((ticket, index) => (
            <div key={ticket.id}>
              {ticket.status === "progress" && (
                <div className="ticketBlock">
                  <button
                    onClick={(e) => {
                      const newTickets = tickets.slice();
                      newTickets.splice(index, 1, {
                        ...ticket,
                        status: "done",
                      });
                      setTickets(newTickets);
                    }}
                  >
                    push
                  </button>
                  &nbsp;
                  <Ticket name={ticket.name} status={ticket.status} key={ticket.id} />
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
      <div className="done">
        <h1>Done</h1>
        <div>
          {tickets.map((ticket, index) => (
            <div key={ticket.id}>
              {ticket.status === "done" && (
                <div className="ticketBlock">
                  <button
                    onClick={(e) => {
                      const newTickets = tickets.slice();
                      newTickets.splice(index, 1, {
                        ...ticket,
                        status: "backlog",
                      });
                      setTickets(newTickets);
                    }}
                  >
                    push
                  </button>
                  &nbsp;
                  <Ticket name={ticket.name} status={ticket.status} key={ticket.id} />
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TicketDisplay;
