import React from "react";
import { useContext, useState } from "react";
import { TicketContext } from "../contexts/TicketContext";
import ChangeStatus from "../addData/ChangeStatus";
import Ticket from "../Ticket";

const TicketDisplay = () => {
  const [tickets, setTickets] = useContext(TicketContext);
  console.log(tickets);

  return (
    <div className="display">
      <div className="backlog">
        <h1>Backlog</h1>
        {tickets.map((ticket) => (
          <div key={ticket.id}>{ticket.status === "backlog" && <li>{ticket.name}</li>}</div>
        ))}
      </div>

      <div className="sprint">
        <h1>Sprint</h1>
        <div>
          {tickets.map((ticket) => (
            <div key={ticket.id}>{ticket.status === "sprint" && <li>{ticket.name}</li>}</div>
          ))}
        </div>
      </div>
      <div className="progress">
        <h1>Progress</h1>
        <div>
          {tickets.map((ticket) => (
            <div key={ticket.id}>{ticket.status === "progress" && <li>{ticket.name}</li>}</div>
          ))}
        </div>
      </div>
      <div className="done">
        <h1>Done</h1>
        <div>
          {tickets.map((ticket, index) => (
            <div key={ticket.id}>
              {ticket.status === "done" && (
                <div>
                  {/* lamda function */}
                  <Ticket name={ticket.name} status={ticket.status} key={ticket.id} />
                  <button
                    onClick={(e) => {
                      const newTickets = tickets.slice();
                      newTickets.splice(index, 1, {
                        ...ticket,
                        status: "backlog",
                      });
                      setTickets(newTickets);
                    }}
                  ></button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TicketDisplay;
