import React from "react";
import { useContext, useState } from "react";
import { TicketContext } from "../contexts/TicketContext";
import uuid from "uuid/v1";
import axios from "axios";
//props takes in data from Link, includes data like location /create
const AddBacklog = (props) => {
  const [tickets, setTickets] = useContext(TicketContext);

  const [name, setName] = useState("");

  const handleChange = (e) => {
    setName(e.target.value);
    console.log({ name });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    let newTicket = {
      ticket_name: name,
      ticket_status: "backlog",
    };

    axios.post("http://localhost:4000/tickets/add", newTicket).then((res) => {
      newTicket = res.data;
      console.log("meee", res.data.ticket._id);
      // let newTickety = {
      //   _id: res.data.ticket._id,
      //   ticket_name: name,
      //   ticket_status: "backlog",
      // };
    });

    console.log("meee", newTicket);
    setTickets((currentTickets) => [...currentTickets, { _id: uuid(), ticket_name: name, ticket_status: "backlog" }]);

    setName("");
    // props.history.push("/");
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="addTicket">
        <label>
          <p>Add Ticket:</p>
          <input type="text" name="name" value={name} onChange={handleChange} />
        </label>
        <input type="submit" name="Submit" className="submit"></input>
      </div>
    </form>
  );
};

export default AddBacklog;
