  const [name, setName] = useState("");

  const handleChange = (e) => {
    setName(e.target.value);
    console.log({ name });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    let newTicket = {
      ticket_name: name,
      ticket_status: "backlog",
    };
    setTicket(newTicket);
    console.log(ticket);
    console.log(newTicket);

    axios.post("http://localhost:4000/tickets/add", ticket).then((res) => {
      setTicket({
        _id: res.data.ticket._id,
        ticket_name: res.data.ticket.ticket_name,
        ticket_status: res.data.ticket.ticket_status,
      });
      console.log("meee", res.data);
    });

    console.log("meee", ticket);
    setTickets((currentTickets) => [...currentTickets, { ticket }]);

    setName("");
    // props.history.push("/");
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="addTicket">
        <label>
          <p>Add Ticket:</p>
          <input type="text" name="name" value={name} onChange={handleChange} />
        </label>
        <input type="submit" name="Submit" className="submit"></input>
      </div>
    </form>
  );
};

export default AddBacklog;
