import React from "react";
import axios from "axios";
import { useState } from "react";

const EditTicket = (props) => {
  const [ticket, setTicket] = useState(props);
  const [name, setName] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:4000/tickets/" + props.ticket.ticket_id)
      .then((response) => {
        console.log(response.data);
        setTicket(response.data);
        // this.setState({ tickets: response.data });
        // setTickets((currentTickets) => [...currentTickets, { name: name, status: "backlog", id: uuid() }]);
        // const [tickets, setTickets] = useState(response.data);
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  const handleChange = (e) => {
    setName(e.target.value);
    console.log({ name });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const newTicket = {
      ticket_name: name,
      ticket_status: "backlog",
    };

    axios.post("http://localhost:4000/tickets/add", newTicket).then((res) => console.log(res.data));

    setTickets((currentTickets) => [...currentTickets, { name: name, status: "backlog", id: uuid() }]);
    setName(""); //empties textbox
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Ticket Name: <input type="text" name="name" value={name} onChange={handleChange} />
      </label>
      <input type="submit" name="Submit"></input>
    </form>
  );
};

export default EditTicket;
