import React from "react";
import axios from "axios";
import { useState, useEffect } from "react";

const EditTicket = (props) => {
  const [ticket, setTicket] = useState("");
  const [name, setName] = useState("");
  useEffect(() => {
    axios
      .get("http://localhost:4000/tickets/" + props.match.params.id)
      .then((response) => {
        setTicket(response.data);
        console.log({ ticket });
      })
      .catch(function (error) {
        console.log(error);
      });
  }, []);

  const handleChange = (e) => {
    setName(e.target.value);
    console.log({ name });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log({ ticket });

    axios.post("http://localhost:4000/tickets/update/" + ticket.ticket_id, updatedTicket).then((res) => console.log(res.data));

    setTickets((currentTickets) => [...currentTickets, { name: name, status: "backlog", id: uuid() }]);
    setName(""); //empties textbox
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Ticket Name: <input type="text" name="name" value={name} onChange={handleChange} />
      </label>
      <input type="submit" name="Submit"></input>
    </form>
  );
};
export default EditTicket;
