import React from "react";
import { useContext, useState } from "react";
import { TicketContext } from "../contexts/TicketContext";
import ChangeStatus from "../addData/ChangeStatus";
import Ticket from "../Ticket";

const TicketDisplay = () => {
  const [tickets, setTickets] = useContext(TicketContext);
  console.log(tickets);

  const push = (id) => {
    setTickets((currentTickets) => [...currentTickets, { name: ticket.value, status: "backlog", id: uuid() }]);
  };

  return (
    <div className="display">
      <div className="backlog">
        <h1>Backlog</h1>
        <div>
          {tickets.map((ticket) => (
            <div key={ticket.id}>{ticket.status === "backlog" && <li>{ticket.name}</li>}</div>
          ))}
        </div>
      </div>

      <div className="sprint">
        <h1>Sprint</h1>
        <div>
          {tickets.map((ticket) => (
            <div key={ticket.id}>{ticket.status === "sprint" && <li>{ticket.name}</li>}</div>
          ))}
        </div>
      </div>
      <div className="progress">
        <h1>Progress</h1>
        <div>
          {tickets.map((ticket) => (
            <div key={ticket.id}>{ticket.status === "progress" && <li>{ticket.name}</li>}</div>
          ))}
        </div>
      </div>
      <div className="done">
        <h1>Done</h1>
        <div>
          {tickets.map((ticket) => (
            <div key={ticket.id}>
              {ticket.status === "done" && (
                <div>
                  {/* lamda function */}
                  <Ticket name={ticket.name} status={ticket.status} key={ticket.id} push={() => push(ticket.id)} />
                  <ChangeStatus value={ticket.name} />
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default TicketDisplay;
